// 第三方
import gulp from 'gulp';
import clean from 'gulp-clean';

// 自定义
import util from '../util.js';
import config from '../config.js';

/************************************ 目录清理 ************************************/

// 清理临时目录
gulp.task('task_clean:temp', () => {
  console.log(`>>>>>>>>>>>>>>> 开始清理临时目录。${util.getNow()}`);
  return gulp.src(config.path.temp).pipe(clean());
});

// 清理发布目录
gulp.task('task_clean:dist', () => {
  console.log(`>>>>>>>>>>>>>>> 开始清理发布目录。${util.getNow()}`);
  return gulp.src(config.path.dist).pipe(clean());
});
