// 第三方
import gulp from 'gulp';
import size from 'gulp-size';
import webpack from 'webpack';
import rev from 'swg-gulp-rev';
import eslint from 'gulp-eslint';
import uglify from 'gulp-uglify';
import replace from 'gulp-replace';
import webpackStream from 'webpack-stream';

// 自定义
import util from '../util.js';
import config from '../config.js';
import eslintrc from '../eslintrc.js';

/************************************ 构建JS任务 ************************************/
gulp.task('task_dll', () => {
  console.log(`>>>>>>>>>>>>>>> js文件开始编译。${util.getNow()}`);

  return gulp.src(config.path.dll)

    // js压缩混淆
    // .pipe(config.env.mini ? uglify() : util.gulpNothing())

    // 显示编译后js文件体积
    .pipe(size({showFiles: true}))

    // 收集js文件的MD5值
    .pipe(config.env.mini ? rev() : util.gulpNothing())

    // 输出js文件
    .pipe(gulp.dest(`${config.path.static}/common`))

    // 输出js文件与MD5值匹配关系的文件
    .pipe(config.env.mini ? rev.manifest('rev-md5/dll.json') : util.gulpNothing())
    .pipe(config.env.mini ? gulp.dest(config.path.temp) : util.gulpNothing())
    ;
});
