// 第三方
import gulp from 'gulp';
import gzip from 'gulp-gzip';

// 自定义
import util from '../util.js';
import config from '../config.js';

/************************************ gzip包 ************************************/
gulp.task('task_gzip', () => {
  console.log(`>>>>>>>>>>>>>>> gzip任务开始编译。${util.getNow()}`);

  return gulp.src(`${util.getProjectName()}.zip`)

    .pipe(gzip())

    .pipe(gulp.dest(''));
});
