<template>
  <div class="dvd-service-com-go-page-top" @click="toTop">
    <img src="./img/go-top-arrow.png" v-show="show">
  </div>
</template>

<!--组件定义-->
<script>
  import $ from 'jquery';
  import util from 'dvd-service-js-util';

  export default {
    props: {},
    data: function () {
      return {
        show: false,
      }
    },
    methods: {
      toTop() {
        $('html,body').animate({scrollTop: 0}, 500);
      }
    },
    filters: {},
    computed: {},
    watch: {},
    mounted: function () {
      let ts = this;
      window.addEventListener('scroll', function () {
        if (util.getDocumentScrollTop() >= 200) {
          ts.show = true;
        } else {
          ts.show = false;
        }
      }, false);
    }
  }
</script>


<!--样式-->
<style lang="sass" lang="scss" rel="stylesheet/scss">
  @import "../dvd-base-scss-util/dvd-base-scss-util";

  .dvd-service-com-go-page-top {
    img {
      position: fixed;
      margin-left: 3.25rem;
      bottom: .7rem;
      width: .44rem;
      z-index: 5;
    }
  }
</style>
