<!--公共确认框-->
<template>
  <!--满屏容器-->
  <div class="dvd-service-com-popup-share-card" :class="[className]" @click="destroy">
    <!--居中容器-->
    <div class="table-cell">
      <!--弹窗-->
      <div class="box" @click.stop>
        <div class="top-tip">{{topTip}}</div>
        <div class="pic"><img :src="img"></div>
        <div class="bottom-tip">{{bottomTip}}</div>
      </div>
    </div>
  </div>
</template>

<script>
  export default {
    props: {
      // 钩子
      className: {
        type: String,
        default: null
      },
      // 分享卡图片地址，
      img: {
        type: String,
        default: null
      },
      // 上方提示文字，
      topTip: {
        type: String,
        default: '长按保存图片'
      },
      // 下方提示文字，
      bottomTip: {
        type: String,
        default: null
      },
    },
    data() {
      return {}
    },
    computed: {
      titleStyle(){
        if (this.type == 'debug') {
          return {
            '-webkit-user-select': 'text',
            'user-select': 'text'
          }
        } else {
          return null;
        }
      }
    },
    created(){
    },
    mounted() {
      if (this.type == 'prompt') {
        this.$refs.input.focus();
      }
    },
    methods: {
      // 销毁自身
      destroy(){
        this.$destroy();
        this.$el.parentNode.removeChild(this.$el);
      },
      // 确定按钮-点击事件
      okBtnClick(){
        if (this.okBtnCallback) {
          if (this.type == 'prompt') {
            this.okBtnCallback(this.$refs.input.value);
          } else {
            this.okBtnCallback();
          }
        }
        this.destroy();
      },
      // 取消按钮-点击事件
      cancelBtnClick(){
        this.cancelBtnCallback && this.cancelBtnCallback();
        this.destroy();
      }
    },
    filters: {},
    watch: {},
  }
</script>

