    line-height: 1;
    * {
      background: transparent;
    }
    .table-cell {
      display: table-cell;
      vertical-align: middle;
      text-align: center;
      .box {
        display: inline-block;
        width: r(270);
        border-radius: r(4);
        animation: dvd-service-com-popup-share-card-animation 0.5s;
        color: #666;
        .top-tip {
          margin-bottom: r(10);
        }
        .pic {
          img {
            height: r(450);
            box-shadow: 0 0 r(20) rgba(105, 97, 79, 0.5);
          }
        }
        .bottom-tip {
          margin-top: r(10);
        }
      }
    }
  }
</style>
