import Vue from 'vue';
import VueLazyload from 'vue-lazyload';
import './dvd-service-js-img-lazyload.css';

let defaultParam = {
  // loading: '//fe-ws.davdian.com/wap/static/dist/static/common/img/img-lazy-loading.png',
  loading: require('./img/loading-square.png'),
  // error: '//pic.davdian.com/free/2016/12/28/519_360_fdc5daf1d2eab033a50af9f80246da60.png',
  try: 2,
  preLoad: 1.5
};

/**
 * @module dvd-service-js-img-lazyload
 * @author swg [源码地址](http://gitlab.rd.vyohui.com/FE-Service/dvd-service-js-img-lazyload.git)
 */
export default {
  // 懒加载全局设置
  init(param = {}) {
    Vue.use(VueLazyload, Object.assign({}, defaultParam, param));
  },
  // 矩形图片
  rect(src = '') {
    return Object.assign({}, defaultParam, {
      src: src,
      loading: require('./img/loading-rect-1.png'),
    })
  },
  // 矩形图片
  rect(src = '') {
    return Object.assign({}, defaultParam, {
      src: src,
      loading: require('./img/loading-rect-1.png'),
    })
  },
}
