import type from 'dvd-base-js-type';

/**
 * @module dvd-service-js-security
 * @author swg [源码地址](http://gitlab.rd.vyohui.com/FE-Service/dvd-service-js-security.git)
 */
export default {
  /**
   * 功能: 过滤可以注入XSS的html标签
   * @param html 需要过滤的html
   */
  filterXsshtml(html) {
    return type.isString(html) ? html.replace(/(object|iframe|anchor|img)/ig, '') : html;
  }
}
